(192, 'reference_receive_gr', 'Reference Receive', 'Sie haben eine neue Referenz erhalten {site_name}!', 'Hallo,\n\n{username} Bitten Sie, einen Verweis, dass sie ffentlich auf ihre {site_name} profile. {site_name} Ist ein Community-Marktplatz fr Unterknfte, die auf Vertrauen und Reputation aufgebaut sind. Ein Hinweis von Ihnen wrde wirklich helfen, ihren Ruf mit der Gemeinde aufzubauen.\nEin Verweis auf {site_name} kann helfen {username} Finden Sie einen khlen Ort zu bleiben oder Host interessante Reisende.\n\n{click_here} Eine Referenz haben{username}.\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {username},</td>\n</tr>\n<tr>\n<td>\n<p>{other_username} has written a reference for you. Before it goes to your public profile, you can review it and either accept or ignore it. {click_here} to view the reference.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Gre,</p>\n<p>Administrator</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(193, 'host_reservation_notification_gr', 'Reservation notification for host', 'Die Reservierung wurde beantragt  {traveler_name}', 'Hallo {username},\n\n{traveler_name} Buchte das {list_title} place on {book_date} beim {book_time}.\n\nDetails wie folgt,\n\nName des Reisenden : {traveler_name}\nKontakt E-Mail-ID : {traveler_email_id}\nOrtsname: {list_title}\nCheck-In : {checkin}\nCheck-out : {checkout}\nPreis: {market_price}\n\nBitte geben Sie die Besttigung an, indem Sie auf die untenstehende Aktion klicken.\n{action_url}\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>\n<p>Hallo {username} ,</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Buchte das {list_title} place on {book_date} beim {book_time}.</p>\n<br />\n<p>Details wie folgt,</p>\n<p>Name des Reisenden  : {traveler_name}</p>\n<p>Kontakt E-Mail-ID: {traveler_email_id}</p>\n<p>Ortsname: {list_title}</p>\n<p>Check-In  : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Preis : {market_price}</p>\n<br />\n<p>Bitte geben Sie die Besttigung an, indem Sie auf die untenstehende Aktion klicken.</p>\n<p>{action_url}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(194, 'traveller_reservation_notification_gr', 'Reservation notification for  traveller', 'Ihre Buchungsanfrage wird erfolgreich gesendet', 'Hallo {traveler_name},\n\nIhre Reservierungsanfrage wurde erfolgreich an den entsprechenden Host gesendet.\n\nBitte warten Sie auf seine Besttigung.\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Ihre Reservierungsanfrage wurde erfolgreich an den entsprechenden Host gesendet.</p>\n<p>Bitte warten Sie auf seine Besttigung.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(195, 'admin_reservation_notification_gr', 'Reservation notification for  administrator', '{traveler_name} Schickte die Reservierungsanfrage an {host_name}', 'Halo Administrator,\n\n{traveler_name} Schickte die Reservierungsanfrage an {list_title} place on {book_date} at {book_time}.\n\nDetails wie folgt,\n\nName des Reisenden : {traveler_name}\nKontakt E-Mail-ID : {traveler_email_id}\nOrtsname : {list_title}\nCheck-in : {checkin}\nCheck-out : {checkout}\nPreis : {market_price}\nHostname : {host_name}\nHost-Email-Id : {host_email_id} \n\n--\nDanke und Gre,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Halo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Schickte die Reservierungsanfrage an {list_title} place on {book_date} at {book_time}.\n</p>\n<p>Details wie folgt,</p>\n<p>Name des Reisenden : {traveler_name}</p>\n<p>Contact Email Id : {traveler_email_id}</p>\n<p>Ortsname : {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Preis : {market_price}</p>\n<p>Hostname : {host_name}</p>\n<p>Host-Email-Id : {host_email_id}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(196, 'traveler_reservation_granted_gr', 'Traveler : After Reservation granted', 'Glckwunsch! Ihre Reservierungsanfrage wird erteilt.', 'Hallo {traveler_name},\n\nGlckwunsch! Ihre Reservierungsanfrage wird erteilt.{host_name} fr {list_name}.\n\nUnten erwhnten wir seine Kontaktdaten,\n\nVorname : {Fname}\nNachname : {Lname}\nLebe in : {livein}\nPhone No : {phnum}\n\nGenaue Adresse ist,\n\nAdresse : {street_address}\nOptionale Anschrift : {optional_address}\nStadt : {city}\nBundesland : {state}\nLand : {country}\nPostleitzahl : {zipcode}\n\nHost-Nachricht : {comment} \n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Glckwunsch! Ihre Reservierungsanfrage wird erteilt. {host_name} fr {list_name}.</p>\n<p>Unten erwhnten wir seine Kontaktdaten,</p>\n<p>Vorname : {Fname}</p>\n<p>Nachname : {Lname}</p>\n<p>Lebe in: {livein}</p>\n<p>Phone No : {phnum}</p>\n<p>Genaue Adresse ist,</p>\n<p>Adresse: {street_address},</p>\n<p>Optionale Anschrift :{optional_address},</p>\n<p>Stadt : {city},</p>\n<p>Bundesland : {state},</p>\n<p>Land : {country},</p>\n<p>Postleitzahl : {zipcode}</p>\n<p>Host-Nachricht : {comment}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(197, 'traveler_reservation_declined_gr', 'Traveler : After reservation declined', 'Es tut uns leid! Ihre Reservierungsanfrage wird verweigert', 'Hallo {traveler_name},\n\nEs tut uns leid! Ihre Reservierungsanfrage wird verweigert {host_name} fr {list_title}.\n\nHost-Nachricht : {comment} \n\nBald werden wir Sie mit der entsprechenden Zahlung in Verbindung setzen.\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>\nEs tut uns leid! Ihre Reservierungsanfrage wird verweigert {host_name} fr {list_title}.</p>\n<p>Host-Nachricht : {comment}</p>\n<p>Bald werden wir Sie mit der entsprechenden Zahlung in Verbindung setzen.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(198, 'traveler_reservation_cancel_gr', 'Traveler : After reservation canceled', '{traveler_name} Hat Ihre Reservierungsliste storniert', 'Hallo {host_name},\n\nEntschuldige, Deine ({status}) Hat Ihre Reservierungsliste storniert{traveler_name} fr {list_title}.\n\n{user_type} Nachricht : {comment} \n\nSicher, dass wir mit Ihnen bald in Verbindung treten, wenn es irgendein Zahlungsausgleich gibt.\n\nUnd auch wenn Sie irgendwelche anderen Fragen haben, fhlen Sie bitte sich frei, mit uns in Verbindung zu treten.\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hi {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Entschuldige, Deine ({status}) Hat Ihre Reservierungsliste storniert{traveler_name} fr {list_title}.</p>\n<p>{user_type} Nachricht : {comment} </p>\n<p>Sicher, dass wir mit Ihnen bald in Verbindung treten, wenn es irgendein Zahlungsausgleich gibt.\n\n</p>\n<p>Und auch wenn Sie irgendwelche anderen Fragen haben, fhlen Sie bitte sich frei, mit uns in Verbindung zu treten.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
