(202, 'host_reservation_granted_gr', 'Host : After Reservation Granted', 'Sie haben die Reservierungsanfrage {traveler_name} akzeptiert', 'Hallo {hostname},\n\nSie haben die Reservierungsanfrage {traveler_name} fr {list_title} akzeptiert.\n\nUnten erwhnten wir seine Kontaktdaten,\n\nVorname: {Fname}\nNachname: {Lname}\nLeben in: {livein}\nTelefonnummer: {phnum}\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style = "width: 100%;" cellspacing = "10" cellpadding = "0">\n<tbody>\n<tr>\n<td> Hallo {Hostname}, </td>\n</tr>\n<tr>\n<td>\n<p> Sie haben die Reservierungsanfrage {traveler_name} fr {list_title} akzeptiert. </p>\n<p> Im Folgenden haben wir seine Kontaktdaten erwhnt, </p>\n<p> Vorname: {Fname} </p>\n<p> Nachname: {Lname} </p>\n<p> Leben in: {livein} </p>\n<p> Telefonnummer: {phnum} </p>\n</td>\n</tr>\n<tr>\n<td>\n<p style = "margin: 0 10px 0 0;"> - </p>\n<p style = "margin: 0 0 10px 0;"> Danke und Gre, </p>\n<p style = "margin: 0 0 10px 0;"> Administrator, </p>\n<p style = "margin: 0px;"> {site_name} </p>\n</td>\n</tr>\n</tbody>\n</table>'),
(203, 'admin_reservation_granted_gr', 'Admin : After Reservation granted', '{host_name} Akzeptiert {traveler_name} Reservierungsanfrage', 'Hallo Administrator,\n\n{host_name} Akzeptiert {traveler_name} Reservierungsanfrage\n{list_title}.\n\n--\nDanke und Gre,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name} Akzeptiert {traveler_name} Reservierungsanfrage\n{list_title}.\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(204, 'host_reservation_declined_gr', 'Host : After Reservation Declined', 'Sie haben die {traveler_name} Reservierungsanfrage', 'Hallo {host_name},\n\nSie haben die {traveler_name} Reservierungsanfrage pour {list_title}.\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Sie haben die {traveler_name} Reservierungsanfrage pour {list_title}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(205, 'admin_reservation_declined_gr', 'Admin : After Reservation Declined', '{host_name} Lehnte die {traveler_name} Buchungsanfrage fr', 'Hallo Administrator,\n\n{host_name} Lehnte die {traveler_name} Buchungsanfrage fr{list_title}.\n\n--\nDanke und Gre,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name} Lehnte die{traveler_name} Buchungsanfrage fr {list_title}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(206, 'admin_reservation_cancel_gr', 'Admin : After reservation canceled', '{traveler_name} Storniert die {host_name} Reservierungsliste', 'Hallo Administrator,\n\n{traveler_name} Storniert die {host_name} Reservierungsliste ({status}) fur {list_title}.\n\n{penalty}\n\n--\nDanke und Gre,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Storniert die {host_name} Reservierungsliste ({status}) fur {list_title}.\n<br/><br/>{penalty}\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(207, 'host_reservation_cancel_gr', 'Host : After reservation canceled', '{host_name} Annulliert Ihre Reservierungsliste', 'Hallo {traveler_name},\n\n{host_name}  Annulliert Ihre Reservierungsliste ({status}) fur {list_title}.\n\n\nWenn Sie irgendwelche anderen Fragen haben, fhlen Sie bitte sich frei, mit uns in Verbindung zu treten.\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hi {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name}  Annulliert Ihre Reservierungsliste ({status}) fur {list_title}.<br /></p>\n<p>Wenn Sie irgendwelche anderen Fragen haben, fhlen Sie bitte sich frei, mit uns in Verbindung zu treten.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Gre,</p>\n<p>Admin,</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(208, 'forgot_password_gr', 'Forgot Password', 'Passwort vergessen', 'Liebes Mitglied,\n\nIm Folgenden haben wir Ihre Kontodaten erwhnt.\n\nAuf geht''s,\n\nEmail_id : {email}\n\nPasswort : {password}\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Liebes Mitglied,</td>\n</tr>\n<tr>\n<td>\n<p>Im Folgenden haben wir Ihre Kontodaten erwhnt.</p>\n<p>Auf geht''s,</p>\n<p>Email_id : {email}</p>\n<p>Passwort : {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Gre,</p>\n<p>Administrator</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(209, 'checkin_host_gr', 'Host: Check In', 'Anreise', 'Hallo {host_name},\n\n{traveler_name} Ist checkin zu {list_title}.\n\nBenutzername: {guest_name}\nListennamen : {list_title}\nPreis : {currency}{price}\nCheckin-Datum : {checkin}\nCheckout-Datum : {checkout}\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name},</td>\n</tr>\n<tr>\n<td>\n<br />\n{traveler_name} Ist checkin zu{list_title} .<br /><br />\nBenutzername : {traveler_name}.<br /><br />\nListennamen : {list_title}.<br /><br />\nPreis : {currency}{price}.<br /><br />\nCheckin-Datum : {checkin}.<br /><br />\nCheckout-Datum : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Gre,</p>\n<p>{site_name} Mannschaft</p>\n<div></div>\n</td>\n</tr>\n</tbody>\n</table>');
