(211, 'reset_password_gr', 'Reset Password', 'Passwort zurcksetzen', 'Liebes Mitglied,\n\nIm Folgenden haben wir Ihre neuen Kontoinformationen erwhnt.\n\nAuf geht''s,\n\nPasswort : {password}\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Liebes Mitglied,</td>\n</tr>\n<tr>\n<td>\n<p>Im Folgenden haben wir Ihre neuen Kontoinformationen erwhnt.</p>\n<p>Auf geht''s,</p>\n<p>Passwort : {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(212, 'admin_payment_gr', 'Admin payment to Host', 'Admin bezahlt Ihre Gebhren fr {list_title}', 'Hallo {username},\n\nAdmin bezahlt Ihre Gebhren fr {list_title}.\n\nDetails wie folgt,\n\nOrtsname : {list_title}\nCheck-in : {checkin}\nCheck-out : {checkout}\nPreis : {payed_price}\nZahlung durch : {payment_type}\nZahl-ID: {pay_id}\nGezahltes Datum: {payed_date}\n\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>\n<p>hallo {username} ,</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Admin bezahlt Ihre Gebhren fr {list_title}.</p>\n<br />\n<p>Details wie folgt,</p>\n<p>Ortsname : {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Preis : {payed_price}</p>\n<p>Zahlung durch : {payment_type}</p>\n<p>Zahl-ID : {pay_id}</p>\n<p>Gezahltes Datum : {payed_date}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(213, 'contact_form_gr', 'Contact Form', 'Nachricht vom Kontaktformular erhalten', 'Hallo Administrator,\n\nEine Nachricht erhalten Sie von uns {date} at {time}.\n\nDetails wie folgt,\n\nName : {name}\n\nEmail : {email}\n\nNachricht : {message}\n\n--\nDanke und Gre,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Admin,</td>\n</tr>\n<tr>\n<td>\n<p>Eine Nachricht erhalten Sie von uns {date} at {time}.</p>\n<p>Details wie folgt,</p>\n<p>Name : {name}</p>\n<p>Email : {email}</p>\n<p>Nachricht : {message}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(214, 'invite_friend_gr', 'Invite My Friends', '{username} laden Sie ein.', 'Hallo Friend''s,\n\n{username} Mchte, dass Sie einladen {site_name}\n\n{dynamic_content}\n\n{click_here}\n\n--\nGre,\n{username}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Friends,</td>\n</tr>\n<tr>\n<td>\n<p>{username} Mchte, dass Sie einladen</p>\n<p>{site_name}</p>\n<p>{dynamic_content}</p>\n<p>{click_here}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Gre,</p>\n<p style="margin: 0px;">{username}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(215, 'email_verification_gr', 'Email Verification Link', '{site_name} E-Mail-Besttigungslink', 'Hallo {user_name},\n\nBitte klicken Sie auf den untenstehenden Link fr Ihre E-Mail-Besttigung {site_name}.\n\n{click_here}\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {user_name},</td>\n</tr>\n<tr>\n<td>\n<p>Bitte klicken Sie auf den untenstehenden Link fr Ihre E-Mail-Besttigung {site_name}.</p>\n<p>{click_here}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(216, 'referral_credit_gr', 'Referral Credit', 'Sie verdienen {amount} Von Verweisen', 'Hallo {username},\n\nSie verdienen die {amount} by {friend_name}\n\n--\nDanke und Gre,\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {user_name},</td>\n</tr>\n<tr>\n<td><p>\nSie verdienen die {amount} by {friend_name}</p>\n</td>\n</tr><tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(217, 'User_join_to_Referal_mail_gr', 'User join to Referal mail', 'Deine Freund-Anmeldung', 'sehr geehrter {refer_name},\n\nIhr Freund {friend_name} ist jetzt Mitglied in {site_name} .Now, $ 100 ist Kredit in Ihrem Reisekredit mgliches Konto.\n\n--\nDanke und Gre,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>sehr geehrter {refer_name},</td>\n</tr>\n<tr>\n<td><br />Ihr Freund {friend_name} ist jetzt Mitglied in {site_name} .Now, $ 100 ist Kredit in Ihrem Reisekredit mgliches Konto.<br /><br /></td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(218, 'contact_request_to_host_gr', 'Contact Request to Host', 'Kontaktanfrage', 'Hallo {host_username},\n		\nBitte klicken Sie auf den folgenden Link, um den Benutzer zu kontaktieren : {link}	\n\nListe : {title}\n		\nCheckin-Datum : {checkin}\n		\nCheckout-Datum : {checkout}\n		\nGste : {guest}\n		\nGast-Mitteilung  : {message}\n\n--\nDanke und Gre,\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_username},</td>\n</tr>\n<tr>\n<td>\nBitte klicken Sie auf den folgenden Link, um den Benutzer zu kontaktieren : {link}<br /><br />\n			\nListe : {title}<br /><br />\n		\nCheckin-Datum : {checkin}<br /><br />\n		\nCheckout-Datum : {checkout}<br /><br />\n		\nGste : {guest}<br /><br />\n		\nGast-Mitteilung  : {message}<br /><br /></td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Gre,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(219, 'contact_request_to_guest_gr', 'Contact Request to Guest', 'Kontaktanfrage', 'Hallo {traveller_username},\n\nSie haben Ihre Anfrage an {host_username} gesendet.\n\nListe: {title}\n\nCheckin Datum: {checkin}\n\nKasse Datum: {checkout}\n\nGste: {guest}\n\nIhre Nachricht: {message}\n\n--\nDanke und Gre,\nAdministrator\n{site_name}', '<Table style = "width: 100%;" Cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Hallo {traveller_username}, </ td>\n</ Tr>\n<Tr>\n<Td>\nSie haben Kontaktanfrage an {host_username} gesendet. <br /> <br />\n\nListe: {title} <br /> <br />\n\nEinchecken Datum: {checkin} <br /> <br />\n\nKasse Datum: {checkout} <br /> <br />\n\nGste: {guest} <br /> <br />\n\nIhre Nachricht: {message} <br /> <br /> </ td>\n</ Tr>\n<Tr>\n<Td>\n<P style = "margin: 0 10px 0 0;"> - </ p>\n<P style = "margin: 0 0 10px 0;"> Danke und Gre, </ p>\n<P style = "margin: 0 10px 0 0;"> Administrator </ p>\n<P style = "margin: 0px;"> {site_name} </ p>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>'),
