(319, 'guest_notify_review_it', 'Guest Notification for Review', '{guest_name} ha estratto.', 'Ciao {host_name},\n\n{guest_name} ha checkouted dalla lista {list_name} a {site_name}\n\nE lui  in attesa per la tua opinione.\n\n--\nGrazie e saluti,\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {host_name},</td>\n</tr>\n<tr>\n<td><br /> {guest_name} ha checkouted dalla lista {list_name} a {site_name}<br />E lui  in attesa per la tua opinione.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>{site_name} Squadra</p>\n<div></div>\n</td>\n</tr>\n</tbody>\n</table>'),
(320, 'list_notification_it', 'Notification for calendar update', 'L''elenco {list_name} ha bisogno di un aggiornamento', 'Ciao {host_name},\n\nIl {list_name} avete elencato in {site_name} non hanno alcun aggiornamento nell''ultimo mese.\n\nPer migliorare il posizionamento nei risultati di ricerca si prega di aggiornare l''elenco\n-\nGrazie e saluti,\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {host_name},</td>\n</tr>\n<tr>\n<td>\n<p>\nIl {list_name} avete elencato in {site_name} non hanno alcun aggiornamento nell''ultimo mese.</p>\n<p>\nPer migliorare il posizionamento nei risultati di ricerca si prega di aggiornare l''elenco\n</p>\n\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>\n');
INSERT INTO `email_templates` (`id`, `type`, `title`, `mail_subject`, `email_body_text`, `email_body_html`) VALUES
(321, 'host_notification_it', 'Upcoming notification for host', 'Il {traveler_name} sta avendo una prenotazione sul tuo {list_title} domani', 'Ciao {host_name},\n\n{traveler_name} prenotato il {list_title} luogo domani.\n\nDettagli come segue,\n\nNome dei viaggiatori: {traveler_name}\nContatto e-mail Id: {traveler_email_id}\nLuogo Nome: {list_title}\nIl check-in: {checkin}\nPartenza: {checkout}\n\n\n--\nGrazie e saluti,\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {host_name},</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} prenotato il  {list_title} luogo domani.</p>\n<p>Dettagli come segue,</p>\n\n<p>Nome dei viaggiatori: {traveler_name}</p>\n<p>Contatto e-mail Id: {traveler_email_id}</p>\n<p>Luogo Nome: {list_title}</p>\n<p>Il check-in:{checkin}</p>\n<p>Partenza : {checkout}</p>\n\n\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>\n'),
(322, 'tc_book_to_admin_po', 'Admin notification for  Travel cretid booking', '{traveler_name} enviou o pedido de reserva usando seu Travel Cretids', 'Ol, Admin,\n\n{traveler_name} enviou o pedido de reserva para reservar o {list_title} lugar em {book_date} em {book_time} usando seus crditos de viagem.\n\nDetalhes como segue,\n\nNome do viajante: {traveler_name}\nId. Do e-mail de contato: {traveler_email_id}\nNome do local: {list_title}\nCheck-in: {checkin}\nConfira: {checkout}\nPreo de mercado: {market_price}\nMontante pago: {payed_amount}\nCrditos de Viagem: {travel_credits}\nNome do host: {host_name}\nID do host de e-mail: {host_email_id}\n\n--\nObrigado e cumprimentos,\n\n{site_name}\nEquipe', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola Admin,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} enviou o pedido de reserva para reservar o {list_title} lugar em {book_date} em {book_time} usando seus crditos de viagem.</p>\n<p>Detalhes como segue,</p>\n<p>Nome do viajante: {traveler_name}</p><P> ID do e-mail de contato: {traveler_email_id} </ p>\n<P> Nome do local: {list_title} </ p>\n<P> Check-in: {checkin} </ p>\n<P> Confira: {checkout} </ p>\n<P> Preo de mercado: {market_price} </ p>\n<P> Montante pago: {payed_amount} </ p>\n<P> Crditos de viagem: {travel_credits} </ p>\n<P> Nome do host: {host_name} </ p>\n<P> ID do host de e-mail: {host_email_id} </ p>\n</ Td>\n</ Tr>\n<Tr>\n<Td>\n<P style = "margin: 0 10px 0 0;"> - </ p>\n<P style = "margin: 0 0 10px 0;"> Obrigado e Atenciosamente, </ p>\n<p style = "margin: 0px;"> {site_name} Equipe </ p>\n</td>\n</tr>\n</tbody>\n</table>'),
(323, 'tc_book_to_host_po', 'Host notification for  Travel cretid booking', ' {traveler_name} Enviou o pedido de reserva utilizando o seu Travel Cretids', 'Ol {username},\n\n{traveler_name} enviou o pedido de reserva para reservar o seu {list_title} lugar em {book_date} em {book_time} usando seus crditos de viagem.\n\nEntraremos em contato com voc com o pagamento apropriado.\n\nDetalhes como segue,\n\nNome do viajante: {traveler_name}\nId. Do e-mail de contato: {traveler_email_id}\nNome do local: {list_title}\nCheck-in: {checkin}\nConfira: {checkout}\nPreo: {market_price}\n\n--\nObrigado e cumprimentos,\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {username} ,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} enviou o pedido de reserva para reservar o seu {list_title} lugar em {book_date} em {book_time} usando seus crditos de viagem.</p>\n<p>Detalhes como segue,</p>\n<p>Nome do viajante: {traveler_name}</p>\n<p>Id. Do e-mail de contato: {traveler_email_id}</p>\n<p>Nome do local:{list_title}</p>\n<p>Check in : {checkin}</p>\n<p>Confira : {checkout}</p>\n<p>Preco : {market_price}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Obrigado e cumprimentos,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(324, 'admin_mass_email_po', 'Admin mass email', '{subject}', 'Oi Usurio,\n\n{dynamic_content}\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Oi Usurio,</td>\n</tr>\n<tr>\n<td>\n<p>{dynamic_content}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Obrigado e cumprimentos,</p>\n<p style="margin: 0 10px 0 0;">Admin</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(325, 'user_reference_po', 'Reference', 'Escreva {username} uma referncia em {site_name}', 'Oi,\n\n{username} est pedindo que voc fornea uma referncia que eles possam exibir publicamente em seu perfil {site_name}. {site_name}  um mercado comunitrio para acomodaes construdo com base na confiana e reputao. Uma referncia de voc realmente ajudaria a construir sua reputao com a comunidade.\nUma referncia em {site_name} pode ajudar {username} a encontrar um lugar legal para hospedar ou hospedar viajantes interessantes.\n\n{click_here} para ter uma referncia para {username}.\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Oi,</td>\n</tr>\n<tr>\n<td>\n<p>{username} est pedindo que voc fornea uma referncia que eles possam exibir publicamente em seu perfil {site_name}. {site_name}  um mercado comunitrio para acomodaes construdo com base na confiana e reputao. Uma referncia de voc realmente ajudaria a construir sua reputao com a comunidade.</p>\n<p>{click_here} para ter uma referncia para {username}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Obrigado e cumprimentos,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(326, 'reference_receive_po', 'Reference Receive', 'Voc recebeu uma nova referncia em {site_name}!', 'Ol {username},\n\n{other_username} escreveu uma referncia para voc. Antes de ir para o seu perfil pblico, voc pode rev-lo e aceit-lo ou ignor-lo. {click_here} para ver a referncia.\n\n--\nObrigado e cumprimentos,\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {username},</td>\n</tr>\n<tr>\n<td>\n<p>{other_username} escreveu uma referncia para voc. Antes de ir para o seu perfil pblico, voc pode rev-lo e aceit-lo ou ignor-lo. {click_here} para ver a referncia.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Obrigado e cumprimentos,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(327, 'host_reservation_notification_po', 'Reservation notification for host', 'A reserva foi solicitada por {traveler_name}', 'Ol {username},\n\n{traveler_name} reservou o {list_title} lugar em {book_date} em {book_time}.\n\nDetalhes como segue,\n\nNome do viajante: {traveler_name}\nId. Do e-mail de contato: {traveler_email_id}\nNome do local: {list_title}\nCheck-in: {checkin}\nConfira: {checkout}\nPreo: {market_price}\n\nPor favor, d a confirmao clicando na ao abaixo.\n{action_url}\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>\n<p>Ola {username} ,</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} reservou o {list_title} lugar em {book_date} em {book_time}.</p>\n<br />\n<p>Detalhes como segue,</p>\n<p>Nome do viajante: {traveler_name}</p>\n<p>Id. Do e-mail de contato:  {traveler_email_id}</p>\n<p>Nome do local : {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Confira : {checkout}</p>\n<p>Preo : {market_price}</p>\n<br />\n<p>Por favor, d a confirmao clicando na ao abaixo.\n{action_url}</p>\n<p>{action_url}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">--\nObrigado e cumprimentos,</p>\n<p style="margin: 0px;">{site_name} Equipe</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(328, 'traveller_reservation_notification_po', 'Reservation notification for  traveller', 'Seu pedido de reserva  enviado com xito', 'Ol {traveler_name},\n\nSua solicitao de reserva  enviada com xito para o host apropriado.\n\nAguarde a sua confirmao.\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Sua solicitao de reserva  enviada com xito para o host apropriado.</p>\n<p>Aguarde a sua confirmao.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Obrigado e cumprimentos,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(329, 'admin_reservation_notification_po', 'Reservation notification for  administrator', '{traveler_name} enviou o pedido de reserva para {host_name}', 'Ol, Admin,\n\n{traveler_name} enviou o pedido de reserva para {list_title} colocar em {book_date} em {book_time}.\n\nDetalhes como segue,\n\nNome do viajante: {traveler_name}\nId. Do e-mail de contato: {traveler_email_id}\nNome do local: {list_title}\nCheck-in: {checkin}\nConfira: {checkout}\nPreo: {market_price}\nNome do host: {host_name}\nID do host de e-mail: {host_email_id}\n\n--\nObrigado e cumprimentos,\n\n{site_name} \nEquipe', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola Admin,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} enviou o pedido de reserva para {list_title} colocar em {book_date} em {book_time}.</p>\n<p>Detalhes como segue,</p>\n<p>Nome do viajante: {traveler_name}</p>\n<p>Id. Do e-mail de contato:  {traveler_email_id}</p>\n<p>Nome do local: {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Confira: {checkout}</p>\n<p>Preo : {market_price}</p>\n<p>Nome do host: {host_name}</p>\n<p>ID do host de e-mail: {host_email_id}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Obrigado e cumprimentos,</p>\n<p style="margin: 0px;">{site_name} Equipe</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(330, 'traveler_reservation_granted_po', 'Traveler : After Reservation granted', 'Parabns! Seu pedido de reserva  concedido.', 'Ol {traveler_name},\n\nParabns, A sua solicitao de reserva  concedida por {host_name} para {list_name}.\n\nAbaixo mencionamos seus detalhes de contato,\n\nPrimeiro nome: {Fname}\nSobrenome: {Lname}\nViver em: {livein}\nTelefone: {phnum}\n\nO endereo exato ,\n\nEndereo: {street_address}\nEndereo opcional: {optional_address}\nCidade: {city}\nEstado: {state}\nPas: {country}\nCdigo postal: {zipcode}\n\nMensagem do Host: {comment}\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Parabns, A sua solicitao de reserva  concedida por {host_name} para {list_name}.</p>\n<p>Abaixo mencionamos seus detalhes de contato,</p>\n<p>Primeiro nome:  {Fname}</p>\n<p>Sobrenome : {Lname}</p>\n<p>Viver em : {livein}</p>\n<p>Telefone: {phnum}</p>\n<p>O endereo exato ,</p>\n<p>Endereo: {street_address},</p>\n<p>Endereo opcional: {optional_address},</p>\n<p>Cidade : {city},</p>\n<p>Estado : {state},</p>\n<p>Pas : {country},</p>\n<p>Cdigo postal : {zipcode}</p>\n<p>Mensagem do Host: {comment}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Obrigado e cumprimentos,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(331, 'traveler_reservation_declined_po', 'Traveler : After reservation declined', 'Desculpa! Seu pedido de reserva  negado', 'Ol {traveler_name},\n\nDesculpe, sua solicitao de reserva  jantada por {host_name} para {list_title}.\n\nMensagem do Host: {comment}\n\nEm breve, entraremos em contato com voc com o pagamento apropriado.\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Desculpe, sua solicitao de reserva  jantada por {host_name} para {list_title}.</p>\n<p>Mensagem do Host: {comment}</p>\n<p>Em breve, entraremos em contato com voc com o pagamento apropriado.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">--\nObrigado e cumprimentos,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(332, 'traveler_reservation_cancel_po', 'Traveler : After reservation canceled', '{traveler_name} cancelou a sua lista de reservas', 'Ol {host_name},\n\nSua lista de reservas ({status})  cancelada por {traveler_name} para {list_title}.\n\n{user_type} Mensagem: {comment}\n\nClaro que entraremos em contato com voc em breve, se houver algum saldo de pagamento.\n\nE tambm, se voc tiver quaisquer outras dvidas, no hesite em contactar-nos.\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Sua lista de reservas ({status})  cancelada por {traveler_name} para {list_title}.</p>\n<p>{user_type} Mensagem: {comment}</p>\n<p>Claro que entraremos em contato com voc em breve, se houver algum saldo de pagamento.</p>\n<p>E tambm, se voc tiver quaisquer outras dvidas, no hesite em contactar-nos.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Obrigado e cumprimentos,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(333, 'traveler_reservation_expire_po', 'Traveler : Reservation Expire', 'Desculpa! O seu pedido de {type} expirou', 'Ol {traveler_name},\n\nSua solicitao {type} expirou.\n\nNome do host: {host_name}\nNome da lista: {list_title}\nPreo: {currency} {price}\nData de Checkin: {checkin}\nData de Sada: {checkout}\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {traveler_name},</td>\n</tr>\n<tr>\n<td>\n<br />\nSua solicitao {type} expirou.<br /><br />\nNome do host: {host_name}.<br /><br />\nNome da lista: {list_title}.<br /><br />\nPreo:  {currency}{price}.<br /><br />\nData de Checkin: {checkin}.<br /><br />\nData de Sada: {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Obrigado e cumprimentos,</p>\n<p>{site_name} Equipe</p>\n<div></div>\n</td>\n</tr>\n</tbody>\n</table>'),
(334, 'host_reservation_expire_po', 'Host : Reservation Expire', '{type} Pedido expirado para a sua lista', 'Ol {host_name},\n\n{type} pedido expirado para {list_title} reservado por {traveler_name}.\n\nNome do hspede: {traveler_name}\nNome da lista: {list_title}\nPreo: {currency} {price}\nData de Checkin: {checkin}\nData de Sada: {checkout}\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola {host_name},</td>\n</tr>\n<tr>\n<td>\n<br />\n{type} pedido expirado para {list_title} reservado por {traveler_name}.<br /><br />\nNome do hspede : {traveler_name}.<br /><br />\nNome da lista : {list_title}.<br /><br />\nPreo : {currency}{price}.<br /><br />\nData de Checkin:  {checkin}.<br /><br />\nData de Sada: {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Obrigado e cumprimentos,</p>\n<p>{site_name} Equipe</p>\n<div></div>\n</td>\n</tr>\n</tbody>\n</table>'),
(335, 'admin_reservation_expire_po', 'Admin : Reservation Expire', '{type} O pedido expirou', 'Ol, Admin,\n\n{traveler_name} A solicitao {type} expirou para {list_title}.\n\nNome do hspede: {traveler_name}\nEmail do hspede: {traveler_email}\nNome do host: {host_name}\nHost Email: {host_email}\nNome da lista: {list_title}\nPreo: {currency} {price}\nData de Checkin: {checkin}\nData de Sada: {checkout}\n\n--\nObrigado e cumprimentos,\n\nAdmin\n{site_name}\n\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ola Admin,</td>\n</tr>\n<tr>\n<td>\n<br />\n{traveler_name} A solicitao {type} expirou para {list_title}.<br /><br />\nNome do hspede: {traveler_name}.<br /><br />\nEmail do hspede: {traveler_email}.<br /><br />\nNome do host: {host_name}.<br /><br />\nHost Email: {host_email}.<br /><br />\nNome da lista : {list_title}.<br /><br />\nPreo : {currency}{price}.<br /><br />\nData de Checkin: {checkin}.<br /><br />\nData de Sada:  {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Obrigado e cumprimentos,</p>\n<p>{site_name} Equipe</p>\n<div></div>\n</td>\n</tr>\n</tbody>\n</table>'),
