(416, 'email_verification_sp', 'Email Verification Link', '{site_name} Enlace de verificacin de correo electrnico', 'Hola {user_name},\n\nPor favor, haga clic en el enlace de abajo para su verificacin de correo electrnico de {site_name}.\n\n{click_here}\n\n-\nGracias y saludos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hola {user_name},</td>\n</tr>\n<tr>\n<td>\n<p>Por favor, haga clic en el enlace de abajo para su verificacin de correo electrnico de {site_name}.</p>\n<p>{click_here}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Gracias y saludos,</p>\n<p style="margin: 0 10px 0 0;">Admin</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(417, 'referral_credit_sp', 'Referral Credit', 'Usted gana {amount} de referencias', 'Hola {username},\n\nSe obtiene {amount} por {friend_name}\n\n--\nGracias y saludos,\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Halo{user_name},</td>\n</tr>\n<tr>\n<td><p>\nSe obtiene {amount} por {friend_name}</p>\n</td>\n</tr><tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Gracias y saludos,</p>\n<p style="margin: 0 10px 0 0;">Admin</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
