(182, 'change password to user_fr', 'admin change to the password', 'Bienvenue sur {site_name}', 'Mot de passe ancien: {new_password} Conform mot de passe: {conform_password} - Merci et salutations, Admin {site_name}', '<Table cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Cher Membre, </ td>\n</ Tr>\n<Tr>\n<Td>\n<P> Vous avez réussi à changer votre mot de passe </ p>\n<P> Veuillez utiliser les détails du mot de passe ci-dessous pour vous connecter. </ P>\n<P> Ancien mot de passe: {new_password} </ p>\n<P> <span> Nouveau mot de passe: {conform_password} </ span> </ p>\n</ Td>\n</ Tr>\n<Tr>\n<Td>\n<P> - </ p>\n<P> Merci et salutations, </ p>\n<P> Admin </ p>\n<P> {site_name} </ p>\n<Div> </ div>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>'),
(183, 'spam_fr', 'Spam List', 'Admin Message - Listing déclaré comme Spam!', '**** MESSAGE D''ADMINISTRATION ****\n\n\n\nLa liste ci-dessous a été signalée comme Spam:\n\nURL de la liste des spams: {list_url}', '<Table cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> **** MESSAGE D''ADMINISTRATION **** </ td>\n</ Tr>\n<Tr>\n<Td>\n<P> RAPPORT SPAM FLEXIFLAT.COM! </ P>\n<P> La liste ci-dessous a été signalée comme Spam: <br /> <br /> {list_url} </ p>\n</ Td>\n</ Tr>\n<Tr>\n<Td> </ td>\n</ Tr>\n</ Tbody>\n</ Table>\n<p> </p>'),
(184, 'host_notify_review_fr', 'Host Notification for Review', '{guest_name} a une révision de {host_name}', 'Salut {guest_name},\n\n{host_name} a ajouté un avis sur vous dans {list_name} sur {site_name}\n\nEt il attend votre avis.\nNom d''hôte: {host_name}\nNom de liste: {list_name}\n\n\n-\nMerci et salutations,\nAdmin\n{site_name}\n', '<Table cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Bonjour {guest_name}, </ td>\n</ Tr>\n<Tr>\n<Td> <br/> {host_name} a ajouté un avis sur vous dans {list_name} sur {site_name} <br /> Et il attend votre avis.\n<br/>\nNom d''hôte: {host_name} <br/>\nNom de liste: {list_name}\n</ Td>\n</ Tr>\n<Tr>\n<Td>\n<P> - </ p>\n<P> Merci et salutations, </ p>\n<P> {site_name} Équipe </ p>\n<Div> </ div>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>'),
(185, 'guest_notify_review_fr', 'Guest Notification for Review', '{guest_name} a vérifié.', 'Bonjour {host_name},\n\n{guest_name} a coché à partir de votre liste {list_name} sur {site_name}\n\nEt il attend votre avis.\n\n-\nMerci et salutations,\nAdmin\n{site_name}', '<Table cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Salut {host_name}, </ td>\n</ Tr>\n<Tr>\n<Td> <br /> {guest_name} a checké depuis votre liste {list_name} sur {site_name} <br /> Et il attend votre avis. </ Td>\n</ Tr>\n<Tr>\n<Td>\n<P> - </ p>\n<P> Merci et salutations, </ p>\n<P> {site_name} Équipe </ p>\n<Div> </ div>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>'),
(186, 'list_notification_fr', 'Notification for calendar update', 'La liste {list_name} nécessite une mise à jour', 'Bonjour {host_name},\n\nLe {list_name} que vous avez indiqué dans {site_name} n''a aucune mise à jour le mois dernier.\n\nPour améliorer votre classement dans les résultats de recherche, veuillez mettre à jour votre liste\n-\nMerci et salutations,\n\nAdmin\n{site_name}', '<Table cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Bonjour {host_name}, </ td>\n</ Tr>\n<Tr>\n<Td>\n<P>\nLe {list_name} que vous avez indiqué dans {site_name} n''a aucune mise à jour le mois dernier. </ P>\n<P>\nPour améliorer votre classement dans les résultats de recherche, veuillez mettre à jour votre liste\n</ P>\n\n</ Td>\n</ Tr>\n<Tr>\n<Td>\n<P> - </ p>\n<P> Merci et salutations, </ p>\n<P> Admin </ p>\n<P> {site_name} </ p>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>\n'),
(187, 'host_notification_fr', 'Upcoming notification for host', 'Le {traveler_name} a une réservation sur votre {list_title} demain', 'Bonjour {host_name},\n\n{traveler_name} a réservé la place {list_title} demain.\n\nDétails comme suit,\n\nNom du voyageur: {traveler_name}\nIdentifiant du courriel de contact: {traveler_email_id}\nNom de la localité: {list_title}\nDate d''arrivée: {checkin}\nVérifiez: {checkout}\n\n\n-\nMerci et salutations,\n\nAdmin\n{site_name}', '<Table cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Bonjour {host_name}, </ td>\n</ Tr>\n<Tr>\n<Td>\n<P> {traveler_name} a réservé la place {list_title} demain. </ P>\n<P> Détails comme suit, </ p>\n\n<P> Nom du voyageur: {traveler_name} </ p>\n<P> Identifiant du courriel de contact: {traveler_email_id} </ p>\n<P> Nom de lieu: {list_title} </ p>\n<P> Arrivée: {checkin} </ p>\n<P> Consultez: {checkout} </ p>\n\n\n</ P>\n</ Td>\n</ Tr>\n<Tr>\n<Td>\n<P> - </ p>\n<P> Merci et salutations, </ p>\n<P> Admin </ p>\n<P> {site_name} </ p>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>\n'),
(188, 'tc_book_to_admin_gr', 'Admin notification for  Travel cretid booking', '{traveler_name} a envoyé la demande de réservation en utilisant ses Travel Cretids', 'Bonjour Administrator,\n\n{traveler_name} a envoyé la demande de réservation pour réserver la place {list_title} le {book_date} à {book_time} en utilisant ses Crédits de voyage.\n\nDétails comme suit,\n\nNom du voyageur: {traveler_name}\nIdentifiant du courriel de contact: {traveler_email_id}\nNom de la localité: {list_title}\nDate d''arrivée: {checkin}\nVérifiez: {checkout}\nPrix ​​du marché: {market_price}\nMontant payé: {payed_amount}\nCrédits de voyage: {travel_credits}\nNom d''hôte: {host_name}\nIdentifiant du courrier hôte: {host_email_id}\n\n-\nMerci et salutations,\n\n{site_name}\nMannschaft', '<Table style = "width: 100%;" Cellspacing = "10" cellpadding = "0">\n<Tbody>\n<Tr>\n<Td> Bonjour administrateur, </ td>\n</ Tr>\n<Tr>\n<Td>\n<P> {traveler_name} a envoyé la demande de réservation pour réserver la place {list_title} le {book_date} à {book_time} en utilisant ses Travel Credits. </ P>\n<P> Détails comme suit, </ p>\n<P> Nom du voyageur: {traveler_name} </ p>\n<P> Identifiant du courriel de contact: {traveler_email_id} </ p>\n<P> Nom de lieu: {list_title} </ p>\n<P> Arrivée: {checkin} </ p>\n<P> Consultez: {checkout} </ p>\n<P> Prix du marché: {market_price} </ p>\n<P> Montant payé: {payed_amount} </ p>\n<P> Crédits de voyage: {travel_credits} </ p>\n<P> Nom d''hôte: {host_name} </ p>\n<P> Identifiant du courrier hôte: {host_email_id} </ p>\n</ Td>\n</ Tr>\n<Tr>\n<Td>\n<P style = "margin: 0 10px 0 0;"> - </ p>\n<P style = "margin: 0 0 10px 0;"> Merci et salutations, </ p>\n<P style = "margin: 0px;"> {site_name} Équipe </ p>\n</ Td>\n</ Tr>\n</ Tbody>\n</ Table>'),
(189, 'tc_book_to_host_gr', 'Host notification for  Travel cretid booking', '{traveler_name} hat die Reservierungsanfrage unter Verwendung seiner Travel Cretids gesendet', 'Hallo {username},\n\n{traveler_name}hat die Reservierungsanfrage unter Verwendung seiner Travel Cretids gesendet {list_title} place on {book_date} at {book_time} Unter Verwendung seiner Reisekredite.\n\nWir setzen uns mit Ihnen in Verbindung.\n\nDetails wie folgt,\n\nName des Reisenden: {traveler_name}\nKontakt E-Mail-ID : {traveler_email_id}\nOrtsname : {list_title}\nCheck-in : {checkin}\nCheck-out : {checkout}\nPreis : {market_price}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {username} ,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name}hat die Reservierungsanfrage unter Verwendung seiner Travel Cretids gesendet {list_title} place on {book_date} at {book_time} Unter Verwendung seiner Reisekredite.</p>\n<p>Wir setzen uns mit Ihnen in Verbindung,</p>\n<p>Name des Reisenden : {traveler_name}</p>\n<p>Kontakt E-Mail-ID: {traveler_email_id}</p>\n<p>Ortsname: {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Presis : {market_price}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(190, 'admin_mass_email_gr', 'Admin mass email', '{subject}', 'Hallo Benutzer,\n\n{dynamic_content}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Benutzer,</td>\n</tr>\n<tr>\n<td>\n<p>{dynamic_content}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(191, 'user_reference_gr', 'Reference', 'Schreiben {username} Ein Verweis auf {site_name}', 'Hallo,\n\n{username} Bitten Sie, einen Verweis, dass sie öffentlich auf ihre {site_name} profile. {site_name} Ist ein Community-Marktplatz für Unterkünfte, die auf Vertrauen und Reputation aufgebaut sind. Ein Hinweis von Ihnen würde wirklich helfen, ihren Ruf mit der Gemeinde aufzubauen.\nEin Verweis auf {site_name} kann helfen {username} Finden Sie einen kühlen Ort zu bleiben oder Host interessante Reisende.\n\n{click_here} Eine Referenz haben{username}.\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo,</td>\n</tr>\n<tr>\n<td>\n<p>{username} Bitten Sie, einen Verweis, dass sie öffentlich auf ihre {site_name} profile. {site_name} Ist ein Community-Marktplatz für Unterkünfte, die auf Vertrauen und Reputation aufgebaut sind. Ein Hinweis von Ihnen würde wirklich helfen, ihren Ruf mit der Gemeinde aufzubauen. Finden Sie einen kühlen Ort zu bleiben oder Host interessante Reisende.</p>\n<p>{click_here} Eine Referenz haben {username}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(192, 'reference_receive_gr', 'Reference Receive', 'Sie haben eine neue Referenz erhalten {site_name}!', 'Hallo,\n\n{username} Bitten Sie, einen Verweis, dass sie öffentlich auf ihre {site_name} profile. {site_name} Ist ein Community-Marktplatz für Unterkünfte, die auf Vertrauen und Reputation aufgebaut sind. Ein Hinweis von Ihnen würde wirklich helfen, ihren Ruf mit der Gemeinde aufzubauen.\nEin Verweis auf {site_name} kann helfen {username} Finden Sie einen kühlen Ort zu bleiben oder Host interessante Reisende.\n\n{click_here} Eine Referenz haben{username}.\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {username},</td>\n</tr>\n<tr>\n<td>\n<p>{other_username} has written a reference for you. Before it goes to your public profile, you can review it and either accept or ignore it. {click_here} to view the reference.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>Administrator</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(193, 'host_reservation_notification_gr', 'Reservation notification for host', 'Die Reservierung wurde beantragt  {traveler_name}', 'Hallo {username},\n\n{traveler_name} Buchte das {list_title} place on {book_date} beim {book_time}.\n\nDetails wie folgt,\n\nName des Reisenden : {traveler_name}\nKontakt E-Mail-ID : {traveler_email_id}\nOrtsname: {list_title}\nCheck-In : {checkin}\nCheck-out : {checkout}\nPreis: {market_price}\n\nBitte geben Sie die Bestätigung an, indem Sie auf die untenstehende Aktion klicken.\n{action_url}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>\n<p>Hallo {username} ,</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Buchte das {list_title} place on {book_date} beim {book_time}.</p>\n<br />\n<p>Details wie folgt,</p>\n<p>Name des Reisenden  : {traveler_name}</p>\n<p>Kontakt E-Mail-ID: {traveler_email_id}</p>\n<p>Ortsname: {list_title}</p>\n<p>Check-In  : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Preis : {market_price}</p>\n<br />\n<p>Bitte geben Sie die Bestätigung an, indem Sie auf die untenstehende Aktion klicken.</p>\n<p>{action_url}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(194, 'traveller_reservation_notification_gr', 'Reservation notification for  traveller', 'Ihre Buchungsanfrage wird erfolgreich gesendet', 'Hallo {traveler_name},\n\nIhre Reservierungsanfrage wurde erfolgreich an den entsprechenden Host gesendet.\n\nBitte warten Sie auf seine Bestätigung.\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Ihre Reservierungsanfrage wurde erfolgreich an den entsprechenden Host gesendet.</p>\n<p>Bitte warten Sie auf seine Bestätigung.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(195, 'admin_reservation_notification_gr', 'Reservation notification for  administrator', '{traveler_name} Schickte die Reservierungsanfrage an {host_name}', 'Halo Administrator,\n\n{traveler_name} Schickte die Reservierungsanfrage an {list_title} place on {book_date} at {book_time}.\n\nDetails wie folgt,\n\nName des Reisenden : {traveler_name}\nKontakt E-Mail-ID : {traveler_email_id}\nOrtsname : {list_title}\nCheck-in : {checkin}\nCheck-out : {checkout}\nPreis : {market_price}\nHostname : {host_name}\nHost-Email-Id : {host_email_id} \n\n--\nDanke und Grüße,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Halo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Schickte die Reservierungsanfrage an {list_title} place on {book_date} at {book_time}.\n</p>\n<p>Details wie folgt,</p>\n<p>Name des Reisenden : {traveler_name}</p>\n<p>Contact Email Id : {traveler_email_id}</p>\n<p>Ortsname : {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Preis : {market_price}</p>\n<p>Hostname : {host_name}</p>\n<p>Host-Email-Id : {host_email_id}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(196, 'traveler_reservation_granted_gr', 'Traveler : After Reservation granted', 'Glückwunsch! Ihre Reservierungsanfrage wird erteilt.', 'Hallo {traveler_name},\n\nGlückwunsch! Ihre Reservierungsanfrage wird erteilt.{host_name} für {list_name}.\n\nUnten erwähnten wir seine Kontaktdaten,\n\nVorname : {Fname}\nNachname : {Lname}\nLebe in : {livein}\nPhone No : {phnum}\n\nGenaue Adresse ist,\n\nAdresse : {street_address}\nOptionale Anschrift : {optional_address}\nStadt : {city}\nBundesland : {state}\nLand : {country}\nPostleitzahl : {zipcode}\n\nHost-Nachricht : {comment} \n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Glückwunsch! Ihre Reservierungsanfrage wird erteilt. {host_name} für {list_name}.</p>\n<p>Unten erwähnten wir seine Kontaktdaten,</p>\n<p>Vorname : {Fname}</p>\n<p>Nachname : {Lname}</p>\n<p>Lebe in: {livein}</p>\n<p>Phone No : {phnum}</p>\n<p>Genaue Adresse ist,</p>\n<p>Adresse: {street_address},</p>\n<p>Optionale Anschrift :{optional_address},</p>\n<p>Stadt : {city},</p>\n<p>Bundesland : {state},</p>\n<p>Land : {country},</p>\n<p>Postleitzahl : {zipcode}</p>\n<p>Host-Nachricht : {comment}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(197, 'traveler_reservation_declined_gr', 'Traveler : After reservation declined', 'Es tut uns leid! Ihre Reservierungsanfrage wird verweigert', 'Hallo {traveler_name},\n\nEs tut uns leid! Ihre Reservierungsanfrage wird verweigert {host_name} für {list_title}.\n\nHost-Nachricht : {comment} \n\nBald werden wir Sie mit der entsprechenden Zahlung in Verbindung setzen.\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>\nEs tut uns leid! Ihre Reservierungsanfrage wird verweigert {host_name} für {list_title}.</p>\n<p>Host-Nachricht : {comment}</p>\n<p>Bald werden wir Sie mit der entsprechenden Zahlung in Verbindung setzen.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(198, 'traveler_reservation_cancel_gr', 'Traveler : After reservation canceled', '{traveler_name} Hat Ihre Reservierungsliste storniert', 'Hallo {host_name},\n\nEntschuldige, Deine ({status}) Hat Ihre Reservierungsliste storniert{traveler_name} für {list_title}.\n\n{user_type} Nachricht : {comment} \n\nSicher, dass wir mit Ihnen bald in Verbindung treten, wenn es irgendein Zahlungsausgleich gibt.\n\nUnd auch wenn Sie irgendwelche anderen Fragen haben, fühlen Sie bitte sich frei, mit uns in Verbindung zu treten.\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hi {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Entschuldige, Deine ({status}) Hat Ihre Reservierungsliste storniert{traveler_name} für {list_title}.</p>\n<p>{user_type} Nachricht : {comment} </p>\n<p>Sicher, dass wir mit Ihnen bald in Verbindung treten, wenn es irgendein Zahlungsausgleich gibt.\n\n</p>\n<p>Und auch wenn Sie irgendwelche anderen Fragen haben, fühlen Sie bitte sich frei, mit uns in Verbindung zu treten.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(199, 'traveler_reservation_expire_gr', 'Traveler : Reservation Expire', 'Es tut uns leid! Ihre{type} Ist abgelaufen', 'Hallo {traveler_name},\n\nIhre {type} Ist abgelaufen.\n\nHostname : {host_name}\nListennamen : {list_title}\nPreis : {currency}{price}\nCheck-in Datum : {checkin}\nCheckout Datum : {checkout}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {traveler_name},</td>\n</tr>\n<tr>\n<td>\n<br />\nIhre {type} Ist abgelaufen.<br /><br />\nHostname : {host_name}.<br /><br />\nListennamen : {list_title}.<br /><br />\nPreis : {currency}{price}.<br /><br />\nCheckin Datum : {checkin}.<br /><br />\nCheckout Datum : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>{site_name} Mannschaft</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(200, 'host_reservation_expire_gr', 'Host : Reservation Expire', '{type} Anforderung für Ihre Liste abgelaufen', 'Hallo {host_name},\n\n{type} Anforderung für Ihre Liste abgelaufen {list_title} Gebucht von {traveler_name}.\n\nBenutzername : {traveler_name}\nListennamen : {list_title}\nPreis : {currency}{price}\nCheckin Datum : {checkin}\nCheckout Datum : {checkout}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name},</td>\n</tr>\n<tr>\n<td>\n<br />\n{type} Anforderung für Ihre Liste abgelaufen {list_title} Gebucht von {traveler_name}.<br /><br />\nBenutzername :  : {traveler_name}.<br /><br />\nListennamen : {list_title}.<br /><br />\nPreis : {currency}{price}.<br /><br />\nCheckin Datum : {checkin}.<br /><br />\nCheckout Datum : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>{site_name} Mannschaft</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(201, 'admin_reservation_expire_gr', 'Admin : Reservation Expire', '{type} Ist abgelaufen', 'Hallo Administrator,\n\n{traveler_name} {type} Anforderung ist abgelaufen {list_title}.\n\nBenutzername : {traveler_name}\nGäste-E-Mail : {traveler_email}\nHostname : {host_name}\nHost-Email : {host_email}\nListtenname : {list_title}\nPreis : {currency}{price}\nCheckin Datum : {checkin}\nCheckout Datum : {checkout}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}\n\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<br />\n{traveler_name} {type} Anforderung ist abgelaufen {list_title}.<br /><br />\nBenutzername : {traveler_name}<br /><br />\nGäste-E-Mail : {traveler_email}.<br /><br />\nHostname : {host_name}.<br /><br />\nHost-email : {host_email}.<br /><br />\nListtenname : {list_title}.<br /><br />\nPreis : {currency}{price}.<br /><br />\nCheckin Datum : {checkin}.<br /><br />\nCheckout Datum : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>{site_name} Mannschaft</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(202, 'host_reservation_granted_gr', 'Host : After Reservation Granted', 'Sie haben die Reservierungsanfrage {traveler_name} akzeptiert', 'Hallo {hostname},\n\nSie haben die Reservierungsanfrage {traveler_name} für {list_title} akzeptiert.\n\nUnten erwähnten wir seine Kontaktdaten,\n\nVorname: {Fname}\nNachname: {Lname}\nLeben in: {livein}\nTelefonnummer: {phnum}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style = "width: 100%;" cellspacing = "10" cellpadding = "0">\n<tbody>\n<tr>\n<td> Hallo {Hostname}, </td>\n</tr>\n<tr>\n<td>\n<p> Sie haben die Reservierungsanfrage {traveler_name} für {list_title} akzeptiert. </p>\n<p> Im Folgenden haben wir seine Kontaktdaten erwähnt, </p>\n<p> Vorname: {Fname} </p>\n<p> Nachname: {Lname} </p>\n<p> Leben in: {livein} </p>\n<p> Telefonnummer: {phnum} </p>\n</td>\n</tr>\n<tr>\n<td>\n<p style = "margin: 0 10px 0 0;"> - </p>\n<p style = "margin: 0 0 10px 0;"> Danke und Grüße, </p>\n<p style = "margin: 0 0 10px 0;"> Administrator, </p>\n<p style = "margin: 0px;"> {site_name} </p>\n</td>\n</tr>\n</tbody>\n</table>'),
(203, 'admin_reservation_granted_gr', 'Admin : After Reservation granted', '{host_name} Akzeptiert {traveler_name} Reservierungsanfrage', 'Hallo Administrator,\n\n{host_name} Akzeptiert {traveler_name} Reservierungsanfrage\n{list_title}.\n\n--\nDanke und Grüße,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name} Akzeptiert {traveler_name} Reservierungsanfrage\n{list_title}.\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(204, 'host_reservation_declined_gr', 'Host : After Reservation Declined', 'Sie haben die {traveler_name} Reservierungsanfrage', 'Hallo {host_name},\n\nSie haben die {traveler_name} Reservierungsanfrage pour {list_title}.\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Sie haben die {traveler_name} Reservierungsanfrage pour {list_title}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 0 10px 0;">Administrator,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(205, 'admin_reservation_declined_gr', 'Admin : After Reservation Declined', '{host_name} Lehnte die {traveler_name} Buchungsanfrage für', 'Hallo Administrator,\n\n{host_name} Lehnte die {traveler_name} Buchungsanfrage für{list_title}.\n\n--\nDanke und Grüße,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name} Lehnte die{traveler_name} Buchungsanfrage für {list_title}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(206, 'admin_reservation_cancel_gr', 'Admin : After reservation canceled', '{traveler_name} Storniert die {host_name} Reservierungsliste', 'Hallo Administrator,\n\n{traveler_name} Storniert die {host_name} Reservierungsliste ({status}) fur {list_title}.\n\n{penalty}\n\n--\nDanke und Grüße,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Administrator,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Storniert die {host_name}  Reservierungsliste ({status}) fur {list_title}.\n<br/><br/>{penalty}\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(207, 'host_reservation_cancel_gr', 'Host : After reservation canceled', '{host_name} Annulliert Ihre Reservierungsliste', 'Hallo {traveler_name},\n\n{host_name}  Annulliert Ihre Reservierungsliste ({status}) fur {list_title}.\n\n\nWenn Sie irgendwelche anderen Fragen haben, fühlen Sie bitte sich frei, mit uns in Verbindung zu treten.\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hi {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name}  Annulliert Ihre Reservierungsliste ({status}) fur {list_title}.<br /></p>\n<p>Wenn Sie irgendwelche anderen Fragen haben, fühlen Sie bitte sich frei, mit uns in Verbindung zu treten.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>Admin,</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(208, 'forgot_password_gr', 'Forgot Password', 'Passwort vergessen', 'Liebes Mitglied,\n\nIm Folgenden haben wir Ihre Kontodaten erwähnt.\n\nAuf geht''s,\n\nEmail_id : {email}\n\nPasswort : {password}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Liebes Mitglied,</td>\n</tr>\n<tr>\n<td>\n<p>Im Folgenden haben wir Ihre Kontodaten erwähnt.</p>\n<p>Auf geht''s,</p>\n<p>Email_id : {email}</p>\n<p>Passwort : {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>Administrator</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(209, 'checkin_host_gr', 'Host: Check In', 'Anreise', 'Hallo {host_name},\n\n{traveler_name} Ist checkin zu {list_title}.\n\nBenutzername: {guest_name}\nListennamen : {list_title}\nPreis : {currency}{price}\nCheckin-Datum : {checkin}\nCheckout-Datum : {checkout}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name},</td>\n</tr>\n<tr>\n<td>\n<br />\n{traveler_name} Ist checkin zu {list_title} .<br /><br />\nBenutzername : {traveler_name}.<br /><br />\nListennamen : {list_title}.<br /><br />\nPreis : {currency}{price}.<br /><br />\nCheckin-Datum : {checkin}.<br /><br />\nCheckout-Datum : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>{site_name} Mannschaft</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>');
