(210, 'users_signin_gr', 'Users Signin', 'Willkommen zu {site_name}', 'Liebes Mitglied,\n\nWir freuen uns auf Ihren Besuch {site_name}.\n\nIm Folgenden haben wir Ihre Kontodaten erwähnt.\n\nAuf geht''s,\n\nEmail_id : {email}\n\nPasswort: {password}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Liebes Mitglied,</td>\n</tr>\n<tr>\n<td>\n<p>Wir freuen uns auf Ihren Besuch {site_name}.</p>\n<p>Im Folgenden haben wir Ihre Kontodaten erwähnt.</p>\n<p>Auf geht''s,</p>\n<p>Email_id : {email}</p>\n<p>Passwort: {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(211, 'reset_password_gr', 'Reset Password', 'Passwort zurücksetzen', 'Liebes Mitglied,\n\nIm Folgenden haben wir Ihre neuen Kontoinformationen erwähnt.\n\nAuf geht''s,\n\nPasswort : {password}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Liebes Mitglied,</td>\n</tr>\n<tr>\n<td>\n<p>Im Folgenden haben wir Ihre neuen Kontoinformationen erwähnt.</p>\n<p>Auf geht''s,</p>\n<p>Passwort : {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(212, 'admin_payment_gr', 'Admin payment to Host', 'Admin bezahlt Ihre Gebühren für {list_title}', 'Hallo {username},\n\nAdmin bezahlt Ihre Gebühren für {list_title}.\n\nDetails wie folgt,\n\nOrtsname : {list_title}\nCheck-in : {checkin}\nCheck-out : {checkout}\nPreis : {payed_price}\nZahlung durch : {payment_type}\nZahl-ID: {pay_id}\nGezahltes Datum: {payed_date}\n\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>\n<p>hallo {username} ,</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Admin bezahlt Ihre Gebühren für {list_title}.</p>\n<br />\n<p>Details wie folgt,</p>\n<p>Ortsname : {list_title}</p>\n<p>Check-in : {checkin}</p>\n<p>Check-out : {checkout}</p>\n<p>Preis : {payed_price}</p>\n<p>Zahlung durch : {payment_type}</p>\n<p>Zahl-ID : {pay_id}</p>\n<p>Gezahltes Datum : {payed_date}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(213, 'contact_form_gr', 'Contact Form', 'Nachricht vom Kontaktformular erhalten', 'Hallo Administrator,\n\nEine Nachricht erhalten Sie von uns {date} at {time}.\n\nDetails wie folgt,\n\nName : {name}\n\nEmail : {email}\n\nNachricht : {message}\n\n--\nDanke und Grüße,\n\n{site_name} Mannschaft', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Admin,</td>\n</tr>\n<tr>\n<td>\n<p>Eine Nachricht erhalten Sie von uns {date} at {time}.</p>\n<p>Details wie folgt,</p>\n<p>Name : {name}</p>\n<p>Email : {email}</p>\n<p>Nachricht : {message}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0px;">{site_name} Mannschaft</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(214, 'invite_friend_gr', 'Invite My Friends', '{username} laden Sie ein.', 'Hallo Friend''s,\n\n{username} Möchte, dass Sie einladen {site_name}\n\n{dynamic_content}\n\n{click_here}\n\n--\nGrüße,\n{username}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo Friends,</td>\n</tr>\n<tr>\n<td>\n<p>{username} Möchte, dass Sie einladen</p>\n<p>{site_name}</p>\n<p>{dynamic_content}</p>\n<p> {click_here}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grüße,</p>\n<p style="margin: 0px;">{username}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(215, 'email_verification_gr', 'Email Verification Link', '{site_name} E-Mail-Bestätigungslink', 'Hallo {user_name},\n\nBitte klicken Sie auf den untenstehenden Link für Ihre E-Mail-Bestätigung {site_name}.\n\n{click_here}\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {user_name},</td>\n</tr>\n<tr>\n<td>\n<p>Bitte klicken Sie auf den untenstehenden Link für Ihre E-Mail-Bestätigung {site_name}.</p>\n<p> {click_here}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(216, 'referral_credit_gr', 'Referral Credit', 'Sie verdienen {amount} Von Verweisen', 'Hallo {username},\n\nSie verdienen die {amount} by {friend_name}\n\n--\nDanke und Grüße,\nAdministrator\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {user_name},</td>\n</tr>\n<tr>\n<td><p>\nSie verdienen die {amount} by {friend_name}</p>\n</td>\n</tr><tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Danke und Grüße,</p>\n<p style="margin: 0 10px 0 0;">Administrator</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
