(251, 'host_notify_review_gr', 'Host Notification for Review', '{guest_name} Hat eine Bewertung von {host_name}', 'Hallo {guest_name},\n\n{host_name} Hat eine Rezension über Sie in {list_name} auf {site_name} hinzugefügt\n\nUnd er wartet auf Ihre Bewertung.\nHostname : {host_name}\nListename : {list_name}\n\n\n--\nDanke und Grüße,\nAdministrator\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {guest_name},</td>\n</tr>\n<tr>\n<td><br/ > {host_name} Hat eine Rezension über Sie in {list_name} auf {site_name} hinzugefügt <br />Und er wartet auf Ihre Bewertung.\n<br/>\nHostname : {host_name}<br/>\nListename : {list_name}\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>{site_name} Mannschaft</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(252, 'guest_notify_review_gr', 'Guest Notification for Review', '{guest_name} Hat ausgecheckt.', 'Hallo {host_name},\n\n{guest_name} Hat überprüft von Ihrer Liste {list_name} auf {site_name}\n\nUnd er wartet auf Ihre Bewertung.\n\n--\nDanke und Grüße,\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name},</td>\n</tr>\n<tr>\n<td><br /> {guest_name} Hat überprüft von Ihrer Liste {list_name} auf {site_name}<br />Und er wartet auf Ihre Bewertung.</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>{site_name} Mannschaft</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(253, 'list_notification_gr', 'Notification for calendar update', 'Die Liste {list_name} benötigt ein Update', 'Hallo {host_name},\n\nDie {list_name}, die Sie in {site_name} aufgeführt haben, haben keine Updates im letzten Monat.\n\nUm Ihr Ranking in den Suchergebnissen zu verbessern, aktualisieren Sie bitte Ihre Liste\n-\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name},</td>\n</tr>\n<tr>\n<td>\n<p>\nDie {list_name}, die Sie in {site_name} aufgeführt haben, haben keine Updates im letzten Monat.</p>\n<p>\nUm Ihr Ranking in den Suchergebnissen zu verbessern, aktualisieren Sie bitte Ihre Liste. \n</p>\n\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>Administrator</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>\n'),
(254, 'host_notification_gr', 'Upcoming notification for host', '{traveler_name} Hat eine Reservierung auf Ihrem {list_title} auf morgen', 'Hallo {host_name},\n\n{traveler_name} Hat eine Reservierung auf Ihrem {list_title} auf morgen\n\nDetails wie folgt,\n\nName des Reisenden: {traveler_name}\nKontakt E-Mail-ID: {traveler_email_id}\nOrtsname: {list_title}\nEinchecken: {checkin}\nAbreise: {checkout}\n\n\n--\nDanke und Grüße,\n\nAdministrator\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hallo {host_name},</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} Hat eine Reservierung auf Ihrem {list_title} auf morgen</p>\n<p>Details wie folgt,\n</p>\n\n<p>Name des Reisenden : {traveler_name}</p>\n<p>Kontakt E-Mail-ID : {traveler_email_id}</p>\n<p>Ortsname : {list_title}</p>\n<p>Einchecken : {checkin}</p>\n<p>Abreise: {checkout}</p>\n\n\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Danke und Grüße,</p>\n<p>Administrator</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>\n'),
(255, 'tc_book_to_admin_it', 'Admin notification for  Travel cretid booking', ' {traveler_name} ha inviato la richiesta di prenotazione utilizzando i suoi Cretids di viaggio', 'Ciao Admin,\n\n{traveler_name} inviato la richiesta di prenotazione per prenotare la{list_title} metti su {book_date} at {book_time} utilizzando i suoi crediti di viaggio.\n\nDettagli come segue,\n\nNome Traveler : {traveler_name}\nContatto Email Id: {traveler_email_id}\nNome del luogo : {list_title}\nCheck in : {checkin}\nCheck out : {checkout}\nPrezzo di mercato : {market_price}\nsomma pagata: {payed_amount}\nCrediti di viaggio : {travel_credits} \nNome Host : {host_name}\nHost Email Id : {host_email_id} \n\n--\nGrazie e saluti,\n\n{site_name} Squadra', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao Admin,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} inviato la richiesta di prenotazione per prenotare la{list_title} metti su {book_date} at {book_time} utilizzando i suoi crediti di viaggio.</p>\n<p>\nDettagli come segue,</p>\n<p>\nNome Traveler : {traveler_name}</p>\n<p>Contatto Email Id: {traveler_email_id}</p>\n<p>Nome del luogo : {list_title}</p>\n<p>Check in : {checkin}</p>\n<p>Check out : {checkout}</p>\n<p>Prezzo di mercato : {market_price}</p>\n<p>somma pagata : {payed_amount}</p>\n<p>Crediti di viaggio : {travel_credits}</p>\n<p>Nome Host: {host_name}</p>\n<p>Host Email Id : {host_email_id}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0px;">{site_name} Squadra</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(256, 'tc_book_to_host_it', 'Host notification for  Travel cretid booking', ' {traveler_name}inviato la richiesta di prenotazione utilizzando i suoi Cretids di viaggio', 'Ciao {username},\n\n{traveler_name} ha inviato la richiesta di prenotazione per prenotare il tuo {list_title} luogo il {book_date} a {book_time} utilizzando i suoi crediti di viaggio.\n\n\nCi metteremo in contatto con il pagamento appropriata.\n\nDettagli come segue,\n\nNome Traveler : {traveler_name}\nContatto Email Id : {traveler_email_id}\nNome del luogo : {list_title}\nCheck in : {checkin}\nCheck out : {checkout}\nPrezzo : {market_price}\n\n--\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {username} ,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} ha inviato la richiesta di prenotazione per prenotare il tuo {list_title} luogo il {book_date} a {book_time} utilizzando i suoi crediti di viaggio.</p>\n<p>Dettagli come segue,</p>\n<p>Nome Traveler : {traveler_name}</p>\n<p>Contatto Email Id: {traveler_email_id}</p>\n<p>Nome del luogo : {list_title}</p>\n<p>Check in : {checkin}</p>\n<p>Check out : {checkout}</p>\n<p>Prezzo : {market_price}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(257, 'admin_mass_email_it', 'Admin mass email', '{subject}', 'Ciao utente,\n\n{dynamic_content}\n\n--\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao utente,</td>\n</tr>\n<tr>\n<td>\n<p>{dynamic_content}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 10px 0 0;">Admin</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(258, 'user_reference_it', 'Reference', 'Scrivi {username} un riferimento {site_name}', 'Ciao,\n\n{username} si chiede di fornire un punto di riferimento che possano mostrare pubblicamente il loro {site_name} profilo. {site_name} è un mercato comunitario per le sistemazioni che si basa su fiducia e la reputazione. Un riferimento da voi sarebbe davvero contribuire a costruire la loro reputazione con la comunità.\nUn riferimento su {site_name} può aiutare {username} trovare un luogo fresco per rimanere o ospitare i viaggiatori interessanti.\n\n{click_here} di avere un riferimento per {username}.\n\n--\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao,</td>\n</tr>\n<tr>\n<td>\n<p>{username} si chiede di fornire un punto di riferimento che possano mostrare pubblicamente il loro {site_name} profilo. {site_name} è un mercato comunitario per le sistemazioni che si basa su fiducia e la reputazione. Un riferimento da voi sarebbe davvero contribuire a costruire la loro reputazione con la comunità.\nUn riferimento su {site_name} può aiutare {username} trovare un luogo fresco per rimanere o ospitare i viaggiatori interessanti.{click_here} di avere un riferimento per {username}.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(259, 'reference_receive_it', 'Reference Receive', 'Hai ricevuto un nuovo riferimento in {site_name}!', 'Ciao {username},\n\n{other_username} ha scritto un riferimento per voi. Prima si va al tuo profilo pubblico, è possibile rivedere e accettare o ignorarlo. {click_here} per visualizzare il riferimento.\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {username},</td>\n</tr>\n<tr>\n<td>\n<p>{other_username} ha scritto un riferimento per voi. Prima si va al tuo profilo pubblico, è possibile rivedere e accettare o ignorarlo. {click_here} per visualizzare il riferimento.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(260, 'host_reservation_notification_it', 'Reservation notification for host', 'La prenotazione è stata richiesta da {traveler_name}', 'Ciao {username},\n\n{traveler_name} prenotato il {list_title} luogo il {book_date} a \n{book_time}.\n\nDettagli come segue,\n\nNome dei viaggiatori: {traveler_name}\nContatto e-mail Id: {traveler_email_id}\nLuogo Nome: {list_title}\nIl check-in: {checkin}\nPartenza: {checkout}\nPrezzo: {market_price}\n\nSi prega di dare conferma cliccando l''azione di seguito.\n{action_url}\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>\n<p>Ciao {username} ,</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} prenotato il {list_title} luogo il {book_date} a \n{book_time}.</p>\n<br />\n<p>Dettagli come segue,\n</p>\n<p>Nome dei viaggiatori: {traveler_name}</p>\n<p>Contatto e-mail Id: {traveler_email_id}</p>\n<p>Luogo Nome: {list_title}</p>\n<p>Check in : {checkin}</p>\n<p>Partenza: {checkout}</p>\n<p>Prezzo: {market_price}</p>\n<br />\n<p>Si prega di dare conferma cliccando l''azione di seguito.</p>\n<p>{action_url}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0px;">{site_name} Squadra</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(261, 'traveller_reservation_notification_it', 'Reservation notification for  traveller', 'La vostra richiesta di prenotazione viene inviata con successo', 'Ciao {traveler_name},\n\nLa vostra richiesta di prenotazione viene inviata con successo per l''host appropriato.\n\nSi prega di attendere la sua conferma.\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>La vostra richiesta di prenotazione viene inviata con successo per l''host appropriato.\n</p>\n<p>Si prega di attendere la sua conferma.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(262, 'admin_reservation_notification_it', 'Reservation notification for  administrator', '{traveler_name} ha inviato la richiesta di prenotazione a {host_name}', 'Ciao Admin,\n\n{traveler_name} ha inviato la richiesta di prenotazione per {list_title} luogo il {book_date} a {book_time}.\n\nDettagli come segue,\n\nNome dei viaggiatori: {traveler_name}\nContatto e-mail Id: {traveler_email_id}\nLuogo Nome: {list_title}\nIl check-in: {checkin}\nPartenza: {checkout}\nPrezzo: {market_price}\nNome Host: {host_name}\nHost Email Id: {host_email_id}\n\n--\nGrazie e saluti,\n\n{site_name} Squadra', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao Admin,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} ha inviato la richiesta di prenotazione per {list_title} luogo il {book_date} a {book_time}.</p>\n<p>Dettagli come segue,</p>\n<p>Nome dei viaggiatori: {traveler_name}</p>\n<p>Contatto e-mail Id: {traveler_email_id}</p>\n<p>Luogo Nome : {list_title}</p>\n<p>Il check-in: {checkin}</p>\n<p>Partenza : {checkout}</p>\n<p>Prezzo : {market_price}</p>\n<p>Nome Host: {host_name}</p>\n<p>Host Email Id : {host_email_id}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0px;">{site_name} Squadra</p>\n</td>\n</tr>\n</tbody>\n</table>');
INSERT INTO `email_templates` (`id`, `type`, `title`, `mail_subject`, `email_body_text`, `email_body_html`) VALUES
(263, 'traveler_reservation_granted_it', 'Traveler : After Reservation granted', 'Congratulazioni! La vostra richiesta di prenotazione è concesso.', 'Ciao {traveler_name},\n\nComplimenti, la tua richiesta di prenotazione è garantita da {host_name} per {list_name}.\n\nQui di seguito abbiamo accennato i suoi dati di contatto,\n\nNome: {Fname}\nCognome: {Lname}\nLive In: {livein}\nTelefono No: {Phnum}\n\nIndirizzo esatto è,\n\nIndirizzo: {street_address}\nOpzionale Indirizzo: {optional_address}\nCittà: {city}\nStato: {state}\nPaese: {country}\nCAP: {zipcode}\n\nHost Messaggio: {comment}\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Complimenti, la tua richiesta di prenotazione è garantita da {host_name} per {list_name}.</p>\n<p>Qui di seguito abbiamo accennato i suoi dati di contatto,</p>\n<p>Nome : {Fname}</p>\n<p>Cognome : {Lname}</p>\n<p>Live In : {livein}</p>\n<p>Telefone No : {phnum}</p>\n<p>Indirizzo esatto è,</p>\n<p>Indirizzo: {street_address},</p>\n<p>Opzionale Indirizzo:{optional_address},</p>\n<p>Città : {city},</p>\n<p>Stato : {state},</p>\n<p>Paese : {country},</p>\n<p>CAP : {zipcode}</p>\n<p>Host Messaggio : {comment}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(264, 'traveler_reservation_declined_it', 'Traveler : After reservation declined', 'Scusate! La vostra richiesta di prenotazione viene negata', 'Ciao {traveler_name},\n\nSpiacente, la tua richiesta di prenotazione è cenato da {host_name} per {list_title}.\n\nHost Messaggio: {comment}\n\nPresto, Ci metteremo in contatto con il pagamento appropriata.\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Spiacente, la tua richiesta di prenotazione è cenato da {host_name} per {list_title}.</p>\n<p>Host Messaggio: {comment}</p>\n<p>Presto, Ci metteremo in contatto con il pagamento appropriata.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(265, 'traveler_reservation_cancel_it', 'Traveler : After reservation canceled', '{raveler_name} annullato la tua lista di prenotazione', 'Ciao {host_name},\n\nSiamo spiacenti, il tuo ({status}) lista di prenotazione viene annullata da {traveler_name} per {list_title}.\n\n{user_type} Messaggio: {comment}\n\nCerto ti contatteremo presto, se non vi è alcuna bilancia dei pagamenti.\n\nE anche, se avete altre domande, non esitate a contattarci.\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Siamo spiacenti, il tuo ({status}) lista di prenotazione viene annullata da {traveler_name} per {list_title}.</p>\n<p>{user_type} Messaggio: {comment}</p>\n<p>Certo ti contatteremo presto, se non vi è alcuna bilancia dei pagamenti.</p>\n<p>E anche, se avete altre domande, non esitate a contattarci.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(266, 'traveler_reservation_expire_it', 'Traveler : Reservation Expire', 'Scusate! Il tuo tipo {type} richiesta è scaduto', 'Ciao {traveler_name},\n\nIl tuo tipo {type} richiesta è scaduta.\n\nNome Host: {host_name}\nNome elenco: {list_title}\nPrezzo: {currency} {price}\nCheckin Data: {checkin}\nCheckout Date: {checkout}\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {traveler_name},</td>\n</tr>\n<tr>\n<td>\n<br />\nIl tuo tipo {type} richiesta è scaduta.<br /><br />\nNome Host: {host_name}<br /><br />\nNome elenco: {list_title}.<br /><br />\nPrezzo : {currency}{price}.<br /><br />\nCheckin Data : {checkin}.<br /><br />\nCheckout Date : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>{site_name} Squadra</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(267, 'host_reservation_expire_it', 'Host : Reservation Expire', '{type} richiesta scaduto per la vostra lista', 'Ciao {host_name},\n\n{type} Tipo di richiesta scaduto per {list_title} prenotato da  {traveler_name}.\n\nNome Cliente: {traveler_name}\nNome elenco: {list_title}\nPrezzo: {currency} {price}\nCheckin Data: {checkin}\nCheckout Date: {checkout}\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {host_name},</td>\n</tr>\n<tr>\n<td>\n<br />\n{type} Tipo di richiesta scaduto per {list_title} prenotato da  {traveler_name}.<br /><br />\nNome Cliente:{traveler_name}.<br /><br />\nNome elenco: : {list_title}.<br /><br />\nPrezzo:  {currency}{price}.<br /><br />\nCheckin Data: {checkin}.<br /><br />\nCheckout Date:  {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>{site_name} Squadra</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(268, 'admin_reservation_expire_it', 'Admin : Reservation Expire', '{type} richiesta è scaduto', 'Ciao Admin,\n\n{traveler_name} {type} richiesta è scaduto per {list_title}\n\nNome Cliente: {traveler_name}\nOspite Email: {traveler_email}\nNome Host: {host_name}\nHost E-mail: {host_email}\nNome elenco: {list_title}\nPrezzo: {currency} {price}\nCheckin Data: {checkin}\nCheckout Date: {checkout}\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}\n\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao Admin,</td>\n</tr>\n<tr>\n<td>\n<br />\n{traveler_name} {type} richiesta è scaduto per {list_title}.<br /><br />\nNome Cliente: {traveler_name}.<br /><br />\nOspite Email: {traveler_email}.<br /><br />\nNome Host:  {host_name}.<br /><br />\nHost E-mail:  {host_email}.<br /><br />\nNome elenco:  {list_title}.<br /><br />\nPrezzo : {currency}{price}.<br /><br />\nCheckin Data : {checkin}.<br /><br />\nCheckout Date : {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Grazie e saluti,</p>\n<p>{site_name} Team</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(269, 'host_reservation_granted_it', 'Host : After Reservation Granted', 'Hai accettato il {traveler_name} richiesta di prenotazione', 'Ciao {host_name},\n\nHai accettato il {traveler_name} richiesta di prenotazione per \n{list_title}.\n\nQui di seguito abbiamo accennato i suoi dati di contatto,\n\nNome: {Fname}\nCognome: {Lname}\nLive In: {livein}\nTelefono No: {Phnum}\n\n-\nGrazie e saluti,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Ciao {host_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>Hai accettato il {traveler_name} richiesta di prenotazione per \n{list_title}.</p>\n<p>Qui di seguito abbiamo accennato i suoi dati di contatto,</p>\n<p>Nome : {Fname}</p>\n<p>Cogname : {Lname}</p>\n<p>Live In : {livein}</p>\n<p>Telefone No : {phnum}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Grazie e saluti,</p>\n<p style="margin: 0 0 10px 0;">Admin,</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
