(407, 'admin_reservation_cancel_sp', 'Admin : After reservation canceled', '{traveler_name} canceló la lista de reservas {host_name}', 'Hola administrador\n\n{traveler_name} canceló la lista de reservas {host_name} ({status}) para {list_title}.\n\n{penalty}\n\n--\nGracias y saludos,\n\n{site_name} Equipo', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hi Admin,</td>\n</tr>\n<tr>\n<td>\n<p>{traveler_name} canceló la lista de reservas {host_name} ({status}) para {list_title}.\n<br/><br/>{penalty}\n</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Gracias y saludos,</p>\n<p style="margin: 0px;">{site_name} Equipo</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(408, 'host_reservation_cancel_sp', 'Host : After reservation canceled', '{host_name} cancelado Su lista de reservas', 'Hola {traveler_name},\n\n{host_name} cancela la lista de reservas ({status}) de {list_title}.\n\n\nSi tiene alguna otra pregunta, no dude en contactar con nosotros.\n\n--\nGracias y saludos,\n\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hola {traveler_name} ,</td>\n</tr>\n<tr>\n<td>\n<p>{host_name} cancela la lista de reservas ({status}) de {list_title}.<br /></p>\n<p>Si tiene alguna otra pregunta, no dude en contactar con nosotros.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Gracias y saludos,</p>\n<p>Admin,</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(409, 'forgot_password_sp', 'Forgot Password', 'Se te olvidó tu contraseña', 'Querido miembro,\n\nA continuación hemos mencionado los detalles de su cuenta.\n\nAquí vamos,\n\nEmail_id: {email}\n\nContraseña: {contraseña}\n\n--\nGracias y saludos,\n\nAdmin\n{site_name}', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Querido miembro,</td>\n</tr>\n<tr>\n<td>\n<p>A continuación hemos mencionado los detalles de su cuenta.</p>\n<p>Aquí vamos,</p>\n<p>Email_id : {email}</p>\n<p>Contraseña : {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Gracias y saludos,</p>\n<p>Admin</p>\n<p>{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
(410, 'checkin_host_sp', 'Host: Check In', 'Cheque de viajero', 'Hola {host_name},\n\n{traveler_name} se registra en {list_title}.\n\nNombre de invitado: {guest_name}\nNombre de la lista: {list_title}\nPrecio: {currency} {price}\nFecha de llegada: {checkin}\nFecha de pago: {checkout}\n\n--\nGracias y saludos,\n\nAdmin\n{site_name}\n', '<table cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Hola {host_name},</td>\n</tr>\n<tr>\n<td>\n<br />\n{traveler_name} se registra en {list_title}.<br /><br />\nNombre de invitado: {traveler_name}.<br /><br />\nNombre de la lista: {list_title}.<br /><br />\nPrecio : {currency}{price}.<br /><br />\nFecha de llegada: {checkin}.<br /><br />\nFecha de pago: {checkout}.\n</td>\n</tr>\n<tr>\n<td>\n<p>--</p>\n<p>Gracias y saludos,</p>\n<p>{site_name} Equipo</p>\n<div> </div>\n</td>\n</tr>\n</tbody>\n</table>'),
(411, 'users_signin_sp', 'Users Signin', 'Bienvenido a {site_name}', 'Querido miembro,\n\nEs un placer conocerlo y darle la bienvenida a {site_name}.\n\nA continuación hemos mencionado los detalles de su cuenta.\n\nAquí vamos,\n\nEmail_id: {email}\n\nContraseña: {contraseña}\n\n--\nGracias y saludos,\n\nAdmin\n{site_name}', '<table style="width: 100%;" cellspacing="10" cellpadding="0">\n<tbody>\n<tr>\n<td>Querido miembro,</td>\n</tr>\n<tr>\n<td>\n<p>Es un placer conocerlo y darle la bienvenida a {site_name}.</p>\n<p>A continuación hemos mencionado los detalles de su cuenta.</p>\n<p>Aquí vamos,</p>\n<p>Email_id : {email}</p>\n<p>Contraseña : {password}</p>\n</td>\n</tr>\n<tr>\n<td>\n<p style="margin: 0 10px 0 0;">--</p>\n<p style="margin: 0 0 10px 0;">Gracias y saludos,</p>\n<p style="margin: 0 10px 0 0;">Admin</p>\n<p style="margin: 0px;">{site_name}</p>\n</td>\n</tr>\n</tbody>\n</table>'),
